mapper.update:

  UPDATE diary 
  SET diary_title=#{diary_title}, diary_place=#{diary_place},
  diary_content=#{diary_content}, diary_thumb=#{diary_thumb}, 
  diary_file= #{diary_file}
 WHERE diary_no=#{diary_no}

mapper.select:
SELECT COUNT(*) as cnt
FROM diary
WHERE category_no=#{category_no}

mapper.update:
UPDATE diary
SET diary_cnt = diary_cnt + 1
WHERE diary_no=#{diary_no}

UPDATE diary
SET diary_rcnt = diary_rcnt + 1
WHERE diary_no=#{diary_no}

UPDATE diary
SET diary_rcnt = diary_rcnt - 1
WHERE diary_no=#{diary_no}

mapper.select:
SELECT COUNT(*) as cnt FROM diary
WHERE category_no=#{category_no}
where category_no =#{category_no} AND (diary_title LIKE '%' || #{word} || '%' OR diary_place LIKE '%' || #{word} || '%' OR diary_content LIKE '%' || #{word} || '%' )

SELECT diary_no, category_no, diary_title, diary_content, diary_like, diary_thumb, diary_file, diary_cnt, diary_rcnt, diary_rdate, member_nickname, member_image, pet_name, pet_age, pet_kind, r FROM ( SELECT diary_no, category_no, diary_title, diary_content, diary_like, diary_thumb, diary_file, diary_cnt, diary_rcnt, diary_rdate, member_nickname, member_image, pet_name, pet_age, pet_kind, rownum as r FROM ( SELECT d.diary_no, d.category_no, d.diary_title, d.diary_content, d.diary_like, d.diary_thumb, d.diary_file, d.diary_cnt, d.diary_rcnt, d.diary_rdate, m.member_nickname, m.member_image, p.pet_name, p.pet_age, p.pet_kind FROM diary d left join member m on d.member_no= m.member_no left join pet p on d.member_no= p.member_no
WHERE d.category_no=#{category_no}
WHERE d.category_no=#{category_no} AND (d.diary_content LIKE '%' || #{word} || '%' OR d.diary_title LIKE '%' || #{word} || '%' )
ORDER BY d.diary_no ASC ) ) WHERE r >= #{startNum} AND r <= #{endNum}
